import React from "react";
import WormholeMap from '../../assets/textures/wormhole.jpg';
import CloudsMap from '../../assets/textures/cloud.png';
import { TextureLoader } from "three";
import { useLoader, useFrame } from "@react-three/fiber";
import { OrbitControls, Stars } from '@react-three/drei';
import * as THREE from "three";

export function Wormhole(props){

    const [wormholeMap, cloudMap] = useLoader(TextureLoader, [WormholeMap, CloudsMap])
    
    const wormholeRef = useRef();
    const cloudsRef = useRef();

    useFrame(({ clock }) => {
        const elapsedTime = clock.getElapsedTime();
    
        wormholeRef.current.rotation.y = elapsedTime / 6;
        wormholeRef.current.rotation.z = elapsedTime / 2;
        cloudsRef.current.rotation.y = elapsedTime / -8;
        cloudsRef.current.rotation.x = elapsedTime / 20;
      });

    return (
    <>
        {/* <ambientLight intensity={1} /> */}
        <pointLight color="#f6f3ea" position={[2, 0, 5]} intensity={1.2} />
        <Stars radius={300} depth={60} count={20000} factor={7} saturation={0} fade={true} />
        <mesh ref={cloudsRef} position={[0, 0, 0]}>
            <sphereGeometry args={[1.005, 32, 32]} />
            <meshPhongMaterial
            map={cloudMap}
            opacity={0.4}
            depthWrite={true}
            transparent={true}
            />
        </mesh>

        <mesh ref={wormholeRef} position={[0, 0, 0]}>
            <sphereGeometry args={[1, 32, 32]} />
            <meshStandardMaterial map={wormholeMap} />
            <OrbitControls enableZoom={true} 
            enablePan={true} 
            enableRotate={true} 
            zoomSpeed={0.6} 
            panSpeed={0.5} />
        </mesh>
    </>
    );
}
