import './App.css';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import React, { Suspense, useRef, useEffect } from "react";
import { CosmosapiensNFT} from "./components/cosmosapiensNFT/CosmosapiensNFT";
import { Wormhole } from "./components/wormhole/wormhole";
import { Cosmosapiens } from "./components/NFT/cosmosapiens";
import { Galaxy } from './components/galaxy/galaxy';
import { a } from '@react-spring/three';
import { Scroll } from './components/scroll/scroll';

function Camera(props) {
  const ref = useRef()
  const set = useThree((state) => state.set);
  const [y] = Scroll([-10, 10], { domTarget: window });
  useEffect(() => void set({camera: ref.current }), [])
  useFrame(() => ref.current.updateMatrixWorld())
  return <a.perspectiveCamera ref={ref} {...props} position-y={y.to((y) => (y / 500) * 25)} />
}
 
function App() {
  return (
    <div className='screen'>
      <div className='canvas darkBg'>
        <Canvas colorManagement>
          <Camera position={[0, 2, 7.5]} />
          <Suspense fallback={null}>
            <Wormhole />
            <Galaxy />
          </Suspense>
        </Canvas>
      </div>
      <div className='main'>
        <Cosmosapiens />
      </div>
    </div>
  );
}

export default App;
