import './App.css';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import React, { Suspense, useRef, useEffect } from "react";
import { CosmosapiensNFT} from "./components/cosmosapiensNFT/CosmosapiensNFT";
import { Wormhole } from "./components/wormhole/wormhole";
import { Header } from './components/header/header';
import { Galaxy } from './components/galaxy/galaxy';
import { Section } from './components/section/section';
import { a } from '@react-spring/three';
import { Scroll } from './components/scroll/scroll';
import { Html } from '@react-three/drei'
;
function Camera(props) {
  const ref = useRef()
  const set = useThree((state) => state.set);
  const [y] = Scroll([-20, 20], { domTarget: window });
  useEffect(() => void set({camera: ref.current }), [])
  useFrame(() => ref.current.updateMatrixWorld())
  return <a.perspectiveCamera ref={ref} {...props} position-y={y.to((y) => (y / 500) * 50)} />
}

const HTMLContent = () => {
  return(
    
      <Section factor={1.5} offset={1}>
        <Html fullscreen>
          <div className='drei-container'>
            <h1 className='title'>
              H
            </h1>
          </div>
        </Html>
      </Section>
    
  );
};
 
function App() {
  return (
    <div className='screen'>
      <div className='canvas darkBg'>
        <div className='header'>
          <Header />
        </div>
        <Canvas colorManagement camera={{position:[0,10,40], fov: 70}} >
          {/* <Camera fov={75} position={[0, 0, 7.5]} /> */}
          <Suspense fallback={null}>
            <HTMLContent />
            <Wormhole />
            <Galaxy />
          </Suspense>
        </Canvas>
      </div>
      {/* <div className='main'>
        <Cosmosapiens />
      </div> */}
    </div>
  );
}

export default App;
