import './App.css';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import React, { Suspense, useRef, useEffect } from "react";
import { Wormhole } from "./components/wormhole/wormhole";
import { Header } from './components/header/header';
import { Galaxy } from './components/galaxy/galaxy';
import Plane from './components/model/model';
import { Section } from './components/section/section';
import { a } from '@react-spring/three';
import { Scroll } from './components/scroll/scroll';
import { Html, useGLTF, OrbitControls } from '@react-three/drei';


function Camera(props) {
  const ref = useRef()
  const set = useThree((state) => state.set);
  const [y] = Scroll([-20, 20], { domTarget: window });
  useEffect(() => void set({camera: ref.current }), [])
  useFrame(() => ref.current.updateMatrixWorld())
  return <a.perspectiveCamera ref={ref} {...props} position-y={y.to((y) => (y / 500) * 50)} />
};

const StarShip = () => {
  const gltf = useGLTF('/scene.gltf')
  return <primitive object={gltf.scene} dispose={null} />;
};

const HTMLContent = () => {
  return(
      <Section factor={0.5} offset={0.5}>
        <group position={[0,3,0]}>
          <mesh position={[0,3,0]}>
            
          </mesh>
          <Html center>
            <div className='drei-container'>
              <h1 className='title'>
                I
              </h1>
            </div>
          </Html>
        </group>
      </Section>
  );
};
 
function App() {
  return (
    <div className='screen'>
      <div className='canvas darkBg'>
        <div className='header'>
          <Header />
        </div>
        <Canvas colorManagement camera={{position:[0,1,5], fov: 70}} >
        <OrbitControls enableZoom={true} 
            enablePan={true} 
            enableRotate={true} 
            zoomSpeed={0.6} 
            panSpeed={0.5} />
          {/* <Camera fov={75} position={[0, 0, 7.5]} /> */}
          <Suspense fallback={null}>
            <HTMLContent />
            <Wormhole />
            <StarShip /> 
            <Galaxy />
          </Suspense>
        </Canvas>
      </div>
      {/* <div className='main'>
        <Cosmosapiens />
      </div> */}
    </div>
  );
}

export default App;
