import React from "react";
import WormholeMap from '../../assets/textures/wormhole.jpg';
import CloudsMap from '../../assets/textures/earthCloud.png';
import { TextureLoader } from "three";
import { useLoader } from "@react-three/fiber";
import { OrbitControls } from '@react-three/drei';
import * as THREE from "three";

export function Wormhole(props){

    const [wormholeMap, couldMap] = useLoader(TextureLoader, [WormholeMap, CloudsMap])

    return (
    <>
        <ambientLight intensity={0.25} />
        <mesh>
            <sphereGeometry args={[1.05, 32, 32]} />
            <meshStandardMaterial map={CloudsMap} />
        </mesh>

        <mesh>
            <sphereGeometry args={[1, 32, 32]} />
            <meshStandardMaterial map={wormholeMap} />
            <OrbitControls enableZoom={true} 
            enablePan={true} 
            enableRotate={true} 
            zoomSpeed={0.6} 
            panSpeed={0.5} />
        </mesh>
    </>
    );
}
