import './App.css';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import React, { Suspense, useRef, useEffect } from "react";
import { Wormhole } from "./components/wormhole/wormhole";
import { Header } from './components/header/header';
import { Galaxy } from './components/galaxy/galaxy';
import Plane from './components/model/model';
import { Section } from './components/section/section';
import { a } from '@react-spring/three';
import { Scroll } from './components/scroll/scroll';
import { Html, useGLTF, OrbitControls } from '@react-three/drei';


function Dolly() {
  // This one makes the camera move in and out
  useFrame(({ clock, camera }) => {
    camera.position.z = 1 + Math.sin(clock.getElapsedTime()) * 60
    // camera.position.y = 1 + Math.sin(clock.getElapsedTime()) * 30
  })
  return null
}

const StarShip = () => {
  const gltf = useGLTF('/scene.gltf')
  return <primitive object={gltf.scene} dispose={null} position-y={-0.5} scale={[0.2, 0.2, 0.2]} />;
};

const HTMLContent = () => {
  return(
      <Section factor={0.5} offset={0.5}>
        <group position={[0,3,0]}>
          <mesh position={[0,0,0]}>
          <StarShip /> 
          </mesh>
          <Html center>
            <div className='drei-container'>
              <h1 className='title'>
                I
              </h1>
            </div>
          </Html>
        </group>
      </Section>
  );
};
 
function App() {
  return (
    <div className='screen'>
      <div className='canvas darkBg'>
        <div className='header'>
          <Header />
        </div>
        <Canvas colorManagement camera={{position:[0,0,0], fov: 120}} >
        <OrbitControls enableZoom={true} 
            enablePan={true} 
            enableRotate={true} 
            zoomSpeed={0.6} 
            panSpeed={0.5} />
          {/* <Camera fov={75} position={[0, 0, 7.5]} /> */}
          <Suspense fallback={null}>
            <HTMLContent />
            <Wormhole />
            <Dolly />
            <Galaxy />
          </Suspense>
        </Canvas>
      </div>
      {/* <div className='main'>
        <Cosmosapiens />
      </div> */}
    </div>
  );
}

export default App;
